'use strict';

/**
 * 转换字符串到数组
 * @param  {String}   string
 * @param  {String}   spliter
 * @return {Array}
 */
module.exports = function string2Array(string) {
  var spliter = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : ',';

  return String(string).split(spliter).map(function (item) {
    return item.trim();
  }).filter(function (item) {
    return !!item.length;
  });
};