/**
 * inherit
 * @param  {function} parent
 * @param  {object} protoProps
 * @return {function}
 */
module.exports = function inherit (name, parent, protoProps) {
  const child = function () {  return parent.apply(this, arguments) }
  Object.defineProperty(child, 'name', { value: name })
  child.prototype = Object.assign(Object.create(parent.prototype), protoProps)
  child.prototype.constructor = child
  return child
}