// == utils ==
const object2Query = require('./object2Query')
/**
 * 将 object 转化成 search
 * @param  {Object}  object
 * @param  {Boolean} encode
 * @return {String}
 */
module.exports = function object2Search (object, encode=true) {
  const query = object2Query(object, encode)
  return query.length === 0 ? '' : '?' + query
}