/**
 * 将 query 转化成 object
 * @param  {Object}  object
 * @param  {Boolean} encode
 * @return {String}
 */
module.exports = function queryToObject (query, encode=true) {
  const object = {}
  query.split('&').forEach(keyValue => {
    let [key, value] = keyValue.split('=')
    object[key] = encode ? decodeURIComponent(value) : value
  })
  return object
}